procedures.gen_pol_numbers:
PROCEDURE gen_pol_numbers (
      v_prod_code     IN       NUMBER,
      v_brn_code      IN       NUMBER,
      v_uw_yr         IN       NUMBER,
      v_trans_code    IN       VARCHAR2,
      v_policy_no     IN OUT   VARCHAR2,
      v_endos_no      IN OUT   VARCHAR2,
      v_batch_no      IN OUT   NUMBER,
      v_serial        IN       NUMBER,
      v_policy_type   IN       VARCHAR2,
      v_coinsurance IN VARCHAR2,
      v_div_code  IN VARCHAR2
   )
   IS
      v_pol_type         VARCHAR2 (5);
      v_seq              NUMBER;
      v_seqno            VARCHAR2 (35);
      v_brn_sht_length   NUMBER;
      v_src              VARCHAR2 (1);
      v_binderpols_param  VARCHAR2 (1) DEFAULT 'N';
   BEGIN
       BEGIN
         v_binderpols_param :=
                 gin_parameters_pkg.get_param_varchar ('NORMAL_BINDER_POLS_USESAME_SEQ');
      EXCEPTION
         WHEN OTHERS
         THEN
            v_binderpols_param := 'N';
      END;

procedures.get_risk_dates:
PROCEDURE get_risk_dates (
      v_scl_code                 IN       NUMBER,
      v_covt_code                IN       NUMBER,
      v_pol_tot_instlmt          IN       NUMBER,
      v_pro_expiry_period        IN       VARCHAR2,
      v_pol_cover_from           IN       DATE,
      v_pol_cover_to             IN       DATE,
      v_increment                IN       VARCHAR2,
      v_increment_by             IN       NUMBER,
      v_susp_reinstate           IN       VARCHAR2,
      v_susp_reinst_type         IN       VARCHAR2,
      v_risk_pymt_install_pcts   IN OUT   VARCHAR2,
      v_wef_date                 IN OUT   DATE,
      v_wet_date                 IN OUT   DATE,
      v_install_period           IN OUT   NUMBER,
      v_cover_days               IN OUT   NUMBER,
      v_susp_wef                 IN OUT   DATE,
      v_susp_wet                 IN OUT   DATE,
      v_new_pol_wet              OUT      DATE,
      v_pol_install_wet          IN OUT   DATE,
      v_pol_loaded                        VARCHAR2 DEFAULT 'N',
      v_ipu_status               IN       VARCHAR2 DEFAULT NULL
   )
   IS
      v_cvt_install_type        gin_subclass_cover_types.sclcovt_install_type%TYPE;
      v_cvt_max_installs        gin_subclass_cover_types.sclcovt_max_installs%TYPE;
      v_cvt_pymt_install_pcts   gin_subclass_cover_types.sclcovt_pymt_install_pcts%TYPE;
      v_cvt_install_periods     gin_subclass_cover_types.sclcovt_install_periods%TYPE;
      v_install_pct             NUMBER;
      v_pymnt_tot_instlmt       NUMBER;
      v_suspend_days            NUMBER;
      v_install_days            NUMBER;
      v_max_installs            NUMBER;
      v_months_added            NUMBER;
      v_day                     NUMBER;
      v_month                   NUMBER;
      v_year                    NUMBER;
      v_mnth                    NUMBER;
      v_add_days                NUMBER;
      v_reinsdayspremsubs_param NUMBER;
   --v_pol_install_wet DATE;
   BEGIN
   --raise_error ('Error getting cover type details..');

      BEGIN
         SELECT sclcovt_install_type, sclcovt_max_installs,
                sclcovt_pymt_install_pcts, sclcovt_install_periods
           INTO v_cvt_install_type, v_cvt_max_installs,
                v_cvt_pymt_install_pcts, v_cvt_install_periods
           FROM gin_subclass_cover_types
          WHERE sclcovt_covt_code = v_covt_code
            AND sclcovt_scl_code = v_scl_code;
      EXCEPTION
         WHEN OTHERS
         THEN
            raise_error ('Error getting cover type details..');
      END;

procedures.del_quot_risks:
PROCEDURE del_quot_risks (v_qr_code IN NUMBER)
   IS
   BEGIN
      DELETE      gin_quot_risk_excess
            WHERE qre_qr_code = v_qr_code;

      DELETE      gin_quot_risk_clauses
            WHERE qrc_qr_code = v_qr_code;

      DELETE      gin_quot_risk_limits
            WHERE qrl_qr_code = v_qr_code;

      DELETE      gin_quot_risks
            WHERE qr_code = v_qr_code;
   END;

procedures.del_quot_prod:
PROCEDURE del_quot_prod (v_qp_code IN NUMBER)
   IS
      CURSOR qp_risks
      IS
         SELECT qr_code
           FROM gin_quot_risks
          WHERE qr_qp_code = v_qp_code;
   BEGIN
      FOR qpr IN qp_risks
      LOOP
         del_quot_risks (qpr.qr_code);
      END LOOP;

      DELETE      gin_quot_product_taxes
            WHERE qpt_qp_code = v_qp_code;

      DELETE      gin_quot_clauses
            WHERE qc_qp_code = v_qp_code;

      DELETE      gin_quot_products
            WHERE qp_code = v_qp_code;
   END;

procedures.del_quotation:
PROCEDURE del_quotation (v_quot_code IN NUMBER)
   IS
      CURSOR quot_prods
      IS
         SELECT qp_code
           FROM gin_quot_products
          WHERE qp_quot_code = v_quot_code;
   BEGIN
      FOR qps IN quot_prods
      LOOP
         del_quot_prod (qps.qp_code);
      END LOOP;

      DELETE      gin_policy_exceptions
            WHERE gpe_quot_code = v_quot_code;

      DELETE      gin_quotations
            WHERE quot_code = v_quot_code;
   END;

procedures.pop_quick_quot_taxes:
PROCEDURE pop_quick_quot_taxes (v_qqtn_code IN NUMBER, v_prod_code IN NUMBER,v_con_type IN VARCHAR2 DEFAULT NULL)
   IS
      x   NUMBER := 0;

      CURSOR taxes
      IS
         SELECT *
           FROM gin_taxes_types_view
          WHERE (   scl_code IS NULL
                 OR scl_code IN (SELECT clp_scl_code
                                   FROM gin_product_sub_classes
                                  WHERE clp_pro_code = v_prod_code)
                )
            -- AND trnt_mandatory = 'Y'
            --AND trnt_type IN ('PHFUND', 'SD', 'UTL', 'EX','MPSD','MSD','COPHFUND')
            AND trnt_type IN
                   ('UTX', 'SD', 'UTL', 'EX', 'PHFUND','MPSD','MSD', 'ROAD', 'HEALTH',
                    'CERTCHG', 'MOTORTX')
            AND taxr_trnt_code NOT IN (SELECT qqtt_trac_trnt_code
                                         FROM gin_quick_quot_taxes
                                        WHERE qqtt_qqtn_code = v_qqtn_code)
            AND NVL (trnt_apply_nb, 'N') = 'Y'
            AND trnt_code NOT IN (SELECT petx_trnt_code
                                    FROM gin_product_excluded_taxes
                                   WHERE petx_pro_code = v_prod_code);
   BEGIN
--      RAISE_ERROR(v_qqtn_code ||'='|| v_prod_code);
      FOR txs IN taxes
      LOOP
         x := NVL (x, 0) + 1;
--        IF txs.trnt_type='MSD' THEN
--        RAISE_ERROR(v_qqtn_code ||'='|| v_prod_code);
--       END IF;
         INSERT INTO gin_quick_quot_taxes
                     (qqtt_code, qqtt_trac_trnt_code,
                      qqtt_rate, qqtt_rate_type, qqtt_trnt_renewal_endos,
                      qqtt_taxr_code, qqtt_qqtn_code, qqtt_tax_type,
                      qqtt_tl_lvl_code, qqtt_risk_prod_level
                     )
              VALUES (gin_qqtt_code_seq.NEXTVAL, txs.trnt_code,
                      txs.taxr_rate, txs.taxr_rate_type, NULL,
                      txs.taxr_code, v_qqtn_code, 'UTX',
                      'UP', 'P'
                     );
      END LOOP;

     IF NVL(TRIM(v_con_type),'XYZ')='SEA' THEN
         DELETE gin_quick_quot_taxes WHERE QQTT_TRAC_TRNT_CODE ='SD'
        AND qqtt_qqtn_code=v_qqtn_code; 
        UPDATE gin_quick_quot_taxes SET qqtt_tl_lvl_code ='SI' 
        WHERE qqtt_trac_trnt_code ='MPSD'
         AND qqtt_qqtn_code=v_qqtn_code;
     END IF; 

     IF NVL(TRIM(v_con_type),'XYZ')='AIR' THEN
        DELETE gin_quick_quot_taxes WHERE QQTT_TRAC_TRNT_CODE ='MPSD'
        AND qqtt_qqtn_code=v_qqtn_code;
     END IF;


   END;

procedures.process_policy:
PROCEDURE process_policy (
      v_pol_data       IN       policy_tab,
      v_risks_data     IN       risk_tab,
      v_agentcontact   IN       VARCHAR2,
      v_pol_batch_no   OUT      NUMBER
   )
   IS
      v_cnt               NUMBER;
      v_new_ipu_code      NUMBER;
      v_new_polin_code    NUMBER;
      v_exp_flag          VARCHAR2 (2);
      v_uw_yr             VARCHAR2 (1);
      v_open_cover        VARCHAR2 (2);
      v_user              VARCHAR2 (35)
            := pkg_global_vars.get_pvarchar2 ('PKG_GLOBAL_VARS.PVG_USERNAME');
      v_pol_status        VARCHAR2 (5);
      v_row               NUMBER                      := 0;
      v_trans_no          NUMBER;
      v_stp_code          NUMBER;
      v_scl_desc          VARCHAR2 (75);
      v_bind_desc         VARCHAR2 (75);
      v_wet_date          DATE;
      v_pol_renewal_dt    DATE;
      v_pol_no            VARCHAR2 (45);
      v_end_no            VARCHAR2 (45);
      v_batchno           NUMBER;
      v_cur_code          NUMBER;
      v_cur_symbol        VARCHAR2 (15);
      v_cur_rate          NUMBER;
      v_pol_uwyr          NUMBER;
      v_policy_doc        VARCHAR2 (45);
      v_tran_ref_no        VARCHAR2 (45);
      v_serial             VARCHAR2 (45);
      v_endrsd_rsks_tab   gin_stp_pkg.endrsd_rsks_tab;
      v_rsk_sect_data     rsk_sect_tab;
       next_ggts_trans_no    NUMBER;  
      CURSOR rsks
      IS
         SELECT DISTINCT stpr_gis_ipu_code, stpr_action_type, stpr_stp_code,
                         stpr_property_id, stpr_desc, stpr_scl_code,
                         stpr_scl_desc, stpr_cvt_code, stpr_cvt_desc,
                         stpr_bind_code, stpr_bind_desc
                    FROM gin_risk_stp_temp_data
                   WHERE stpr_stp_code = v_stp_code;

      CURSOR rsk_limits (rsk_prp_id IN VARCHAR2)
      IS
         SELECT DISTINCT stpr_stp_code, stpr_property_id, stpr_desc,
                         stpr_scl_code, stpr_scl_desc, stpr_cvt_code,
                         stpr_cvt_desc, stpr_bind_code, stpr_bind_desc,
                         stpr_sect_code, stpr_sect_desc, stpr_limit
                    FROM gin_risk_stp_temp_data
                   WHERE stpr_stp_code = v_stp_code
                     AND stpr_property_id = rsk_prp_id;
   BEGIN
      v_user := NVL (v_agentcontact, v_user);

--raise_error ('No policy data provided..');
      SELECT gin_stp_code_seq.NEXTVAL
        INTO v_stp_code
        FROM DUAL;

      IF v_pol_data.COUNT = 0
      THEN
         raise_error ('No policy data provided..');
      END IF;

      IF v_risks_data.COUNT = 0
      THEN
         raise_error ('No Risk data provided..');
      END IF;

      DBMS_OUTPUT.put_line (1);

      FOR x IN 1 .. v_risks_data.COUNT
      LOOP
         v_scl_desc := v_risks_data (x).ipu_scl_desc;

         IF v_scl_desc IS NULL
         THEN
            SELECT scl_sht_desc
              INTO v_scl_desc
              FROM gin_sub_classes
             WHERE scl_code = v_risks_data (x).ipu_scl_code;
         END IF;

         v_bind_desc := v_risks_data (x).ipu_bind_desc;

         IF v_bind_desc IS NULL
         THEN
            SELECT bind_name
              INTO v_bind_desc
              FROM gin_binders
             WHERE bind_code = v_risks_data (x).ipu_bind_code;
         END IF;

         INSERT INTO gin_risk_stp_temp_data
                     (stpr_stp_code, stpr_property_id,
                      stpr_desc,
                      stpr_scl_code, stpr_scl_desc,
                      stpr_cvt_code,
                      stpr_cvt_desc,
                      stpr_bind_code, stpr_bind_desc,
                      stpr_sect_code,
                      stpr_sect_desc,
                      stpr_limit,
                      stpr_gis_ipu_code,
                      stpr_action_type
                     )
              VALUES (v_stp_code, v_risks_data (x).ipu_property_id,
                      v_risks_data (x).ipu_desc,
                      v_risks_data (x).ipu_scl_code, v_scl_desc,
                      v_risks_data (x).ipu_cvt_code,
                      v_risks_data (x).ipu_cvt_desc,
                      v_risks_data (x).ipu_bind_code, v_bind_desc,
                      v_risks_data (x).ipu_sect_code,
                      v_risks_data (x).ipu_sect_desc,
                      v_risks_data (x).ipu_limit,
                      v_risks_data (x).gis_ipu_code,
                      v_risks_data (x).ipu_action_type
                     );
      END LOOP;

      DBMS_OUTPUT.put_line (2);

      FOR pcount IN 1 .. v_pol_data.COUNT
      LOOP
         IF v_pol_data (pcount).pol_brn_sht_desc IS NULL
         THEN
            raise_error ('PROVIDE THE BRANCH ...');
         END IF;

         IF v_pol_data (pcount).pol_pro_code IS NULL
         THEN
            raise_error ('SELECT THE POLICY PRODUCT ...');
         END IF;

         IF v_pol_data (pcount).pol_wef_dt IS NULL
         THEN
            raise_error ('PROVIDE THE COVER FROM DATE ...');
         END IF;

         DBMS_OUTPUT.put_line (21);
         v_wet_date := v_pol_data (pcount).pol_wet_dt;

         IF v_wet_date IS NULL
         THEN
            v_wet_date :=
               get_wet_date (v_pol_data (pcount).pol_pro_code,
                             v_pol_data (pcount).pol_wef_dt
                            );
         END IF;

         DBMS_OUTPUT.put_line (22);

         IF v_wet_date IS NULL
         THEN
            raise_error ('PROVIDE THE COVER TO DATE ...');
         END IF;

         DBMS_OUTPUT.put_line (23);

         IF     NVL (v_pol_data (pcount).pol_binder_policy, 'N') = 'Y'
            AND v_pol_data (pcount).pol_bind_code IS NULL
         THEN
            raise_error ('YOU HAVE NOT DEFINED THE BORDEREAUX CODE ..');
         END IF;

         DBMS_OUTPUT.put_line (v_pol_data (pcount).pol_wef_dt);
         DBMS_OUTPUT.put_line (TO_CHAR (v_pol_data (pcount).pol_wef_dt));
         DBMS_OUTPUT.put_line
                           (TO_NUMBER (TO_CHAR (v_pol_data (pcount).pol_wef_dt,
                                                'RRRR'
                                               )
                                      )
                           );
         v_pol_uwyr :=
                  TO_NUMBER (TO_CHAR (v_pol_data (pcount).pol_wef_dt, 'RRRR'));
         /*IF v_pol_Data(pcount).POL_UW_YEAR IS NULL OR v_pol_Data(pcount).POL_UW_YEAR = 0 THEN
             RAISE_ERROR('THE UNDERWRITING YEAR MUST BE A VALID YEAR...');
         END IF;*/
         DBMS_OUTPUT.put_line (25);
         v_pol_renewal_dt :=
               get_renewal_date (v_pol_data (pcount).pol_pro_code, v_wet_date);
         v_cur_code := v_pol_data (pcount).pol_cur_code;
         v_cur_rate := v_pol_data (pcount).pol_cur_rate;

         IF v_cur_code IS NULL
         THEN
            v_cur_rate := NULL;

            BEGIN
               SELECT org_cur_code, cur_symbol
                 INTO v_cur_code, v_cur_symbol
                 FROM tqc_organizations, tqc_systems, tqc_currencies
                WHERE org_code = sys_org_code
                  AND org_cur_code = cur_code
                  AND sys_code = 37;
            EXCEPTION
               WHEN OTHERS
               THEN
                  raise_error ('UNABLE TO RETRIEVE THE BASE CURRENCY');
            END;

procedures.process_quotation:
PROCEDURE process_quotation (
         vpol_id      IN       NUMBER,
         v_quo_code   IN OUT   NUMBER,
         v_quo_no     IN OUT   VARCHAR2
      )
      IS
         v_exp_flag         VARCHAR2 (2);
         v_open_cover       VARCHAR2 (2);
         v_user             VARCHAR2 (35)
               := pkg_global_vars.get_pvarchar2 ('PKG_GLOBAL_VARS.PVG_USERNAME');
         v_row              NUMBER        := 0;
         v_wet_date         DATE;
         v_pol_renewal_dt   DATE;
         v_cur_rate         NUMBER;
         v_cur_code         NUMBER;
         v_cur_symbol       VARCHAR2 (15);
         v_pro_sht_desc     VARCHAR2 (25);
         v_pol_uwyr         NUMBER;
         v_qno              NUMBER;
         v_qp_code          NUMBER;
         v_qr_code          NUMBER;
         v_prp_sht_desc     VARCHAR2 (35);
         v_brn_code         NUMBER;
         v_bind_code        NUMBER;
         v_bind_name        VARCHAR2 (35);
         v_rsk_sect_data    rsk_sect_tab;

         CURSOR pol_cur
         IS
            SELECT *
              FROM gin_web_policies, gin_web_risks
             WHERE pol_id = vpol_id AND ipu_pol_id = pol_id;

         CURSOR risk_cur
         IS
            SELECT *
              FROM gin_web_risks
             WHERE ipu_pol_id = vpol_id;

         CURSOR sect_cur (v_ipu_code IN NUMBER)
         IS
            SELECT *
              FROM gin_web_risk_sections
             WHERE pil_ipu_code = v_ipu_code;
      BEGIN
         DBMS_OUTPUT.put_line (5555555);
         DBMS_OUTPUT.put_line (2);

         FOR pol_rec IN pol_cur
         LOOP
            IF pol_rec.ipu_pro_code IS NULL
            THEN
               raise_error ('SELECT THE POLICY PRODUCT ...');
            END IF;

            IF pol_rec.pol_wef_dt IS NULL
            THEN
               raise_error ('PROVIDE THE COVER FROM DATE ...');
            END IF;

            DBMS_OUTPUT.put_line (21);
            v_wet_date := pol_rec.pol_wet_dt;

            IF v_wet_date IS NULL
            THEN
               v_wet_date :=
                         get_wet_date (pol_rec.pol_pro_code, pol_rec.pol_wef_dt);
            END IF;

            DBMS_OUTPUT.put_line (22);

            IF v_wet_date IS NULL
            THEN
               raise_error ('PROVIDE THE COVER TO DATE ...');
            END IF;

            DBMS_OUTPUT.put_line (23);

            IF     NVL (pol_rec.pol_binder_policy, 'N') = 'Y'
               AND pol_rec.pol_bind_code IS NULL
            THEN
               raise_error ('YOU HAVE NOT DEFINED THE BORDEREAUX CODE ..');
            END IF;

            DBMS_OUTPUT.put_line (pol_rec.pol_wef_dt);
            DBMS_OUTPUT.put_line (TO_CHAR (pol_rec.pol_wef_dt));
            DBMS_OUTPUT.put_line (TO_NUMBER (TO_CHAR (pol_rec.pol_wef_dt, 'RRRR'))
                                 );
            v_pol_uwyr := TO_NUMBER (TO_CHAR (pol_rec.pol_wef_dt, 'RRRR'));
   --         IF pol_rec.POL_UW_YEAR IS NULL OR pol_rec.POL_UW_YEAR = 0 THEN
   --             RAISE_ERROR('THE UNDERWRITING YEAR MUST BE A VALID YEAR...');
   --         END IF;
            DBMS_OUTPUT.put_line (25);
            v_pol_renewal_dt :=
                              get_renewal_date (pol_rec.pol_pro_code, v_wet_date);

            IF NVL (pol_rec.pol_add_edit, 'A') = 'A'
            THEN
               BEGIN
                  SELECT TO_NUMBER (   TO_CHAR (SYSDATE, 'RRRR')
                                    || gin_quot_code_seq.NEXTVAL
                                   )
                    INTO v_quo_code
                    FROM DUAL;
               EXCEPTION
                  WHEN OTHERS
                  THEN
                     raise_error
                        ('Unable to generate new quotation sequence GIN_QUOT_CODE_SEQ.NEXTVAL, ...'
                        );
               END;

procedures.create_midterm_trans:
PROCEDURE create_midterm_trans (
      v_old_batch   IN       NUMBER,
      v_batch_no    OUT      NUMBER,
      v_user        IN       VARCHAR2,
      v_eff_date    IN       DATE
   )
   IS
      v_serial             NUMBER (10);
      v_pol_no             VARCHAR2 (26);
      v_ends_no            VARCHAR2 (26);
      v_pol_prefix         VARCHAR2 (15);
      v_new_ipu_code       NUMBER;
      vdummy               NUMBER;
      v_tran_no            NUMBER;
      v_prrd_code          NUMBER;
      v_wef                DATE;
      v_wet                DATE;
      v_ren_date           DATE;
      v_year               NUMBER;
      next_ggts_trans_no   NUMBER;
      v_serialno           VARCHAR2 (26);
      v_tran_ref_no        VARCHAR2 (26); 

      CURSOR cur_pol
      IS
         SELECT *
           FROM gin_policies
          WHERE pol_batch_no = v_old_batch;

      CURSOR cur_taxes
      IS
         SELECT *
           FROM gin_policy_taxes
          WHERE ptx_pol_batch_no = v_old_batch
            AND NVL (ptx_trac_trnt_code, 'XX') != 'SD';

      CURSOR cur_coinsurer
      IS
         SELECT *
           FROM gin_coinsurers
          WHERE coin_pol_batch_no = v_old_batch;

      CURSOR cur_facre_dtls
      IS
         SELECT *
           FROM gin_facre_in_dtls
          WHERE fid_pol_batch_no = v_old_batch;

      CURSOR cur_conditions
      IS
         SELECT *
           FROM gin_policy_lvl_clauses
          WHERE plcl_pol_batch_no = v_old_batch;

      CURSOR cur_insureds
      IS
         SELECT *
           FROM gin_policy_insureds
          WHERE polin_pol_batch_no = v_old_batch;

      CURSOR cur_ipu (v_polin_code NUMBER)
      IS
         SELECT *
           FROM gin_insured_property_unds
          WHERE ipu_pol_batch_no = v_old_batch
            AND ipu_polin_code = v_polin_code;

      CURSOR cur_limits (v_ipu NUMBER)
      IS
         SELECT *
           FROM gin_policy_insured_limits
          WHERE pil_ipu_code = v_ipu;

      CURSOR cur_clauses (v_ipu NUMBER)
      IS
         SELECT *
           FROM gin_policy_clauses
          WHERE pocl_ipu_code = v_ipu;

      CURSOR perils (v_ipu NUMBER)
      IS
         SELECT gpsp_per_code, gpsp_per_sht_desc, gpsp_sec_sect_code,
                gpsp_sect_sht_desc, gpsp_sec_scl_code, gpsp_ipp_code,
                gpsp_ipu_code, gpsp_limit_amt, gpsp_excess_amt
           FROM gin_pol_sec_perils
          WHERE gpsp_ipu_code = v_ipu;

      CURSOR risk_excesses (v_ipu NUMBER)
      IS
         SELECT *
           FROM gin_risk_excess
          WHERE re_ipu_code = v_ipu;

      CURSOR schedules (v_ipu NUMBER)
      IS
         SELECT *
           FROM gin_policy_risk_schedules
          WHERE polrs_ipu_code = v_ipu;
   BEGIN
--      raise_error ('v_old_batch' || v_old_batch);

      IF v_user IS NULL
      THEN
         raise_error ('User not defined.');
      END IF;

      FOR cur_pol_rec IN cur_pol
      LOOP
         IF     NVL (cur_pol_rec.pol_reinsured, 'N') != 'Y'
            AND NVL (cur_pol_rec.pol_loaded, 'N') = 'N'
            AND tqc_interfaces_pkg.get_org_type (37) = 'INS'
         THEN
            raise_error
               ('Reinsurance for the previous transaction on this policy has not been performed/Authorised. Cannot continue..'
               );
         END IF;

         BEGIN
            SELECT pro_policy_prefix
              INTO v_pol_prefix
              FROM gin_products
             WHERE pro_code = cur_pol_rec.pol_pro_code;

            IF v_pol_prefix IS NULL
            THEN
               ROLLBACK;
               raise_error (   'The policy prefix for the product '
                            || cur_pol_rec.pol_pro_sht_desc
                            || ' is not defined in the setup'
                           );
            END IF;
         EXCEPTION
            WHEN NO_DATA_FOUND
            THEN
               ROLLBACK;
               raise_error (   'The product '
                            || cur_pol_rec.pol_pro_sht_desc
                            || ' is not defined in the setup'
                           );
            WHEN OTHERS
            THEN
               ROLLBACK;
               raise_error
                  (   'Unable to retrieve the policy prefix for the product '
                   || cur_pol_rec.pol_pro_sht_desc
                  );
         END;

procedures.gin_policies_prc:
PROCEDURE gin_policies_prc (
        v_pol_no           IN OUT VARCHAR2,
        v_pol_data         IN     web_pol_tab,
        v_agentcontact     IN     VARCHAR2,
