        v_inception_yr                 NUMBER;
        y                              NUMBER;
        vuser                          VARCHAR2 (35)
            := pkg_global_vars.get_pvarchar2 ('PKG_GLOBAL_VARS.pvg_username');
        v_seqno                        VARCHAR2 (35);
        v_brn_sht_length               NUMBER;
        v_growth_type                  VARCHAR2 (5);
