    and comm_trnt_code NOT  in decode(nvl(v_franch_agn_cd,0),0,'UC-U',comm_trnt_code) 
    AND  comm_trnt_code NOT IN decode(nvl(v_franch_agn_cd,0),0,'UNDIFINED','UC-U') 
    AND  comm_trnt_code NOT IN decode(nvl(v_franch_agn_cd,0),0,'UNDIFINED','LTA-U')    
    AND COMM_SCL_CODE= V_SCL_CODE
    AND COMM_ACT_CODE = V_ACT_CODE
    AND COMM_BIND_CODE =V_BIND_CODE;
BEGIN
   SELECT gin_stp_code_seq.NEXTVAL
     INTO v_stp_code
     FROM DUAL;

   IF v_ipu_data.COUNT = 0
   THEN
      raise_error ('No Risk data provided..');
   END IF;

   --FOR pol_cur_rec IN pol_cur
   --LOOP
   BEGIN
      SELECT param_value
        INTO v_autopopltsections_param
        FROM gin_parameters
       WHERE param_name = 'AUTO_POPLT_MAND_SECTIONS';
   EXCEPTION
      WHEN NO_DATA_FOUND
      THEN
         v_autopopltsections_param := 'Y';
      WHEN OTHERS
      THEN
         v_autopopltsections_param := 'Y';
   END;

procedures.gin_rsk_limits_stp:
PROCEDURE gin_rsk_limits_stp (
      v_new_ipu_code   IN   NUMBER,
      v_scl_code       IN   NUMBER,
      v_bind_code      IN   NUMBER,
      v_sect_code      IN   NUMBER,
      v_covt_code      IN   NUMBER,
      v_row            IN   NUMBER,
      v_add_edit       IN   VARCHAR2,
      v_renewal        IN   VARCHAR2,
      v_ncd_level      IN   NUMBER,
      v_limit      IN   NUMBER DEFAULT NULL
   )
   IS
      v_rsk_sect_data            web_sect_tab;
      sect_cursor                sys_refcursor;
      v_sect_sht_desc            gin_sections.sect_sht_desc%TYPE;
      v_sec_code                 gin_sections.sect_code%TYPE;
      v_sect_desc                gin_sections.sect_desc%TYPE;
      v_sect_type                gin_sections.sect_type%TYPE;
      v_type_desc                VARCHAR2 (25);
      v_prr_rate_type            gin_premium_rates.prr_rate_type%TYPE;
      v_prr_rate                 gin_premium_rates.prr_rate%TYPE;
      v_terr_description         VARCHAR2 (5);
      v_prr_prem_minimum_amt     gin_premium_rates.prr_prem_minimum_amt%TYPE;
      v_prr_multiplier_rate      gin_premium_rates.prr_multiplier_rate%TYPE;
      v_prr_division_factor      gin_premium_rates.prr_division_factor%TYPE;
      v_prr_multplier_div_fact   gin_premium_rates.prr_multplier_div_fact%TYPE;
      v_prr_rate_desc            gin_premium_rates.prr_rate_desc%TYPE;
      v_prr_free_limit           gin_premium_rates.prr_free_limit%TYPE;
      v_sec_declaration          gin_subcl_sections.sec_declaration%TYPE;
      v_scvts_order              gin_subcl_covt_sections.scvts_order%TYPE;
      v_prr_prorated_full        gin_premium_rates.prr_prorated_full%TYPE;
      v_prr_si_limit_type        gin_premium_rates.prr_si_limit_type%TYPE;
      v_prr_si_rate              gin_premium_rates.prr_si_rate%TYPE;
   BEGIN
--   RAISE_ERROR(v_scl_code||';;v_sect_code'||v_sect_code||'v_covt_code'||v_covt_code||'v_bind_code'||v_bind_code||'v_new_ipu_code'||v_new_ipu_code);
      sect_cursor :=
         gis_web_pkg.get_sections (v_scl_code,
                                   v_covt_code,
                                   v_bind_code,
                                   v_sect_code,
                                   v_new_ipu_code,
                                   v_ncd_level
                                  );

      LOOP
         EXIT WHEN sect_cursor%NOTFOUND;

         FETCH sect_cursor
          INTO v_sect_sht_desc, v_sec_code, v_sect_desc, v_sect_type,
               v_type_desc, v_prr_rate_type, v_prr_rate, v_terr_description,
               v_prr_prem_minimum_amt, v_prr_multiplier_rate,
               v_prr_division_factor, v_prr_multplier_div_fact,
               v_prr_rate_desc, v_prr_free_limit, v_sec_declaration,
               v_scvts_order, v_prr_prorated_full, v_prr_si_limit_type,
               v_prr_si_rate;

--RAISE_ERROR('v_sect_sht_desc ==== '||v_sect_sht_desc);
         v_rsk_sect_data := web_sect_tab ();
         v_rsk_sect_data.EXTEND (1);
         v_rsk_sect_data (1) :=
            web_sect_rec (NULL,
                          v_new_ipu_code,
                          v_sect_code,
                          v_sect_sht_desc,
                          NULL,
                          v_limit,--NULL,
                          v_prr_rate,
                          NULL,
                          v_prr_rate_type,
                          v_sect_type,
                          v_prr_prem_minimum_amt,
                          NULL,
                          v_prr_multiplier_rate,
                          v_prr_multplier_div_fact,
                          NULL,
                          v_prr_division_factor,
                          'Y',
                          'N',
                          0,
                          v_sec_declaration,
                          v_prr_free_limit,
                          NULL,
                          NULL,
                          v_prr_prorated_full,
                          NULL,
                          v_sect_desc,
                          v_prr_si_limit_type,
                          v_prr_si_rate,
                          NULL,
                          NULL,
                          NULL,
                          NULL,
                          NULL,
                          NULL,
                          NULL,
                          NULL,
                          NULL
                         );
      --RAISE_ERROR(v_rsk_sect_data.COUNT);
      END LOOP;

      IF v_renewal = 'RN'
      THEN
         gin_ren_rsk_limits (v_new_ipu_code,
                             v_scl_code,
                             v_bind_code,
                             v_sect_code,
                             NULL,
                             v_row,
                             'A',
                             v_rsk_sect_data
                            );
      ELSE
         gin_rsk_limits (v_new_ipu_code,
                         v_scl_code,
                         v_bind_code,
                         v_sect_code,
                        NULL,
                         v_row,
                         'A',
                         v_rsk_sect_data
                        );
      END IF;
   END;

procedures.import_data:
PROCEDURE import_data (
      v_brn_code        IN       NUMBER,
      v_brn_sht_desc    IN       VARCHAR2,
      v_fxd_exch_rate   IN       VARCHAR2,
      v_psd_code        IN       NUMBER DEFAULT NULL,
      v_tot_rec         OUT      NUMBER,
      v_success         OUT      NUMBER
   )
   IS
      v_cnt             NUMBER;
      v_prp_code        NUMBER;
      v_sect_code       NUMBER;
      v_sect_desc       VARCHAR2 (35);
      v_pol_tab         gin_stp_pkg.policy_tab;
      v_rsk_tab         gin_stp_pkg.risk_tab;
      v_cur_code        NUMBER;
      v_cur_symbol      VARCHAR2 (35);
      v_agent_code      NUMBER;
      v_agnt_sht_desc   VARCHAR2 (35);
      v_errmsg          VARCHAR2 (400);
      v_pol_batch_no    NUMBER;

      --v_user        VARCHAR2(35):=Pkg_Global_Vars.get_pvarchar2 ('PKG_GLOBAL_VARS.PVG_USERNAME');
      CURSOR cur_recs
      IS
         SELECT *
           FROM gin_pol_stp_data
          WHERE NVL (psd_transfered, 'N') != 'Y'
            AND psd_code =
                   DECODE (NVL (v_psd_code, 0),
                           0, psd_code,
                           NVL (v_psd_code, 0)
                          );
   --CURSOR cur_clnts(vpsd_agnt_client_id IN NUMBER) IS SELECT * FROM GIN_POL_STP_CLNT_DATA WHERE PSC_AGNT_CLNT_ID = vpsd_agnt_client_id;
   BEGIN
      v_tot_rec := 0;
      v_success := 0;

      FOR crecs IN cur_recs
      LOOP
         v_tot_rec := NVL (v_tot_rec, 0) + 1;

         BEGIN
            BEGIN
               SELECT COUNT (1)
                 INTO v_cnt
                 FROM gin_pol_stp_clnt_data
                WHERE psc_agnt_clnt_id = crecs.psd_agnt_client_id;
            EXCEPTION
               WHEN OTHERS
               THEN
                  v_errmsg := 'Error retrieving insured details..' || SQLERRM;
                  raise_application_error (-20001, v_errmsg);
            END;

procedures.del_ren_risk_details:
PROCEDURE del_ren_risk_details (
      v_pol_batch_no   IN   NUMBER,
      v_ipu_code       IN   NUMBER,
      v_pro_code            NUMBER
   )
   IS
      v_successful   NUMBER;
   BEGIN
      --RAISE_ERROR('v_pol_batch_no '||v_pol_batch_no);
         --v_err_pos := 'Specific Details';
      del_spec_details (v_pro_code, v_ipu_code);

      --v_err_pos := 'Risk Level tables';
      DELETE FROM GIN_POLICY_REN_RISK_SERVICES
            WHERE PRS_IPU_CODE = v_ipu_code;

      DELETE FROM gin_ren_policy_clauses
            WHERE pocl_ipu_code = v_ipu_code;

      DELETE FROM gin_ren_policy_insured_limits
            WHERE pil_ipu_code = v_ipu_code;

      DELETE FROM gin_ren_pol_sec_perils
            WHERE gpsp_ipu_code = v_ipu_code;

      DELETE FROM gin_ren_risk_excess
            WHERE re_ipu_code = v_ipu_code;

      DELETE FROM gin_ren_policy_risk_schedules
            WHERE polrs_ipu_code = v_ipu_code;
      DELETE FROM gin_pol_ren_rsk_section_perils
            WHERE PRSPR_IPU_CODE = v_ipu_code;

      DELETE FROM gin_policy_ren_risk_services
            WHERE prs_ipu_code = v_ipu_code;
--    v_err_pos := 'Deleting Risk';
      DELETE FROM gin_ren_insured_property_unds
            WHERE ipu_code = v_ipu_code;
   END;

procedures.del_ren_pol_proc:
PROCEDURE del_ren_pol_proc (v_pol_batch_no IN NUMBER)
   IS
      CURSOR all_pols
      IS
         SELECT pol_batch_no
           FROM gin_ren_policies
          WHERE pol_policy_no = (SELECT pol_policy_no
                                   FROM gin_ren_policies
                                  WHERE pol_batch_no = v_pol_batch_no);

      CURSOR all_risks_cur (vpolbatch_no IN NUMBER)
      IS
         SELECT ipu_code, pol_pro_code
           FROM gin_ren_insured_property_unds, gin_ren_policies
          WHERE ipu_pol_batch_no = pol_batch_no
            AND ipu_pol_batch_no = vpolbatch_no;
   BEGIN
      FOR p IN all_pols
      LOOP
         FOR all_risks_rec IN all_risks_cur (p.pol_batch_no)
         LOOP
            del_ren_risk_details (v_pol_batch_no,
                                  all_risks_rec.ipu_code,
                                  all_risks_rec.pol_pro_code
                                 );
         END LOOP;

         -- Delete The Policy Details
         DELETE FROM gin_ren_policy_diary
               WHERE pd_pol_batch_no = p.pol_batch_no;

         DELETE FROM gin_ren_policy_lvl_clauses
               WHERE plcl_pol_batch_no = p.pol_batch_no;

         DELETE FROM gin_ren_policy_taxes
               WHERE ptx_pol_batch_no = p.pol_batch_no;

         DELETE FROM gin_ren_coinsurers
               WHERE coin_pol_batch_no = p.pol_batch_no;

         DELETE FROM gin_ren_policy_insureds
               WHERE polin_pol_batch_no = p.pol_batch_no;


                DELETE FROM GIN_RENWL_SBUDTLS
               WHERE PDL_POL_BATCH_NO = p.pol_batch_no;

         --Delete the Policy
         DELETE FROM gin_ren_policies
               WHERE pol_batch_no = p.pol_batch_no;

        -- gis_utilities.close_tickets ('P', p.pol_batch_no);
      END LOOP;
   END;

