procedures.del_ren_pol_clause:
PROCEDURE del_ren_pol_clause (v_plcl_code IN NUMBER, v_pol_code IN NUMBER)
   IS
      v_cnt          NUMBER;
      v_count        NUMBER;
      v_trans_no     NUMBER;
      v_trans_code   VARCHAR2 (5);
   BEGIN
      BEGIN
         SELECT ggt_trans_no, ggt_btr_trans_code
           INTO v_trans_no, v_trans_code
           FROM gin_gis_transactions
          WHERE ggt_pol_batch_no = v_pol_code;

         SELECT COUNT (1)
           INTO v_cnt
           FROM gin_subcl_clauses
          WHERE sbcl_cls_code = v_plcl_code;

         SELECT COUNT (1)
           INTO v_count
           FROM gin_scl_cvt_mand_clauses
          WHERE scvtmc_cls_code = v_plcl_code;
      EXCEPTION
         WHEN NO_DATA_FOUND
         THEN
            v_cnt := 0;
            v_count := 0;
         WHEN OTHERS
         THEN
            v_cnt := 0;
            v_count := 0;
      END;

procedures.insert_certificate_charge:
PROCEDURE insert_certificate_charge (
      v_pol_policy_no   IN   VARCHAR2,
      v_pol_endos_no    IN   VARCHAR2,
      v_pol_batch_no    IN   NUMBER,
      v_pro_code        IN   NUMBER,
      v_pol_binder      IN   VARCHAR2 DEFAULT 'N'
   )
   IS
      v_cert_charge      VARCHAR2 (5);
      v_cert_tran_code   VARCHAR2 (15);
      v_tax_type         VARCHAR2 (10);
      v_cnt              NUMBER;
      v_taxr_rate        NUMBER;
   BEGIN
      v_cert_charge :=
            gin_parameters_pkg.get_param_varchar ('CERTIFICATE_CHARGE_ON_EN');
      DBMS_OUTPUT.put_line ('v_cert_charge' || v_cert_charge);

      IF NVL (v_cert_charge, 'N') = 'Y'
      THEN
         v_cert_tran_code :=
            gin_parameters_pkg.get_param_varchar
                                              ('CERTIFICATE_CHARGE_TRAN_CODE');
         DBMS_OUTPUT.put_line ('v_cert_tran_code' || v_cert_tran_code);

         IF v_cert_tran_code IS NOT NULL
         THEN
            BEGIN
               SELECT trnt_type, taxr_rate
                 INTO v_tax_type, v_taxr_rate
                 FROM gin_taxes_types_view
                WHERE taxr_trnt_code = v_cert_tran_code;
            EXCEPTION
               WHEN NO_DATA_FOUND
               THEN
                  raise_error (   'Certificate charge code '
                               || v_cert_tran_code
                               || ' not defined in setups..'
                              );
               WHEN TOO_MANY_ROWS
               THEN
                  raise_error (   'More than one Certificate charge code '
                               || v_cert_tran_code
                               || '  defined in setups..'
                              );
            END;

