   policydebitnotenumber     IN   VARCHAR2,        -- same as the endos number
   policytransactiontype     IN   VARCHAR2,
                    --  NB  - New business, EN  - Endorsements, RN  - Renewals
   policyproduct             IN   VARCHAR2,
   policycoverfrom           IN   DATE,
   policycoverto             IN   DATE,
   policyunderwritingyear    IN   NUMBER,
   policystampduty           IN   NUMBER,
   v_ped_code                IN   NUMBER,
   v_post_trans              IN   VARCHAR2 DEFAULT 'Y',
   policyuwonly              IN   VARCHAR2,
   policydebtowner           IN   VARCHAR2,
   policypromisedate         IN   DATE
)
   RETURN VARCHAR2
IS
   vcount                NUMBER;
   vreturn               VARCHAR2 (1);
   vclntcode             NUMBER;
   vprocnt               NUMBER;
   r_no                  NUMBER;
   r_pol_no              NUMBER;
   v_pol_rec             gin_policies_loading_tab
                                               := gin_policies_loading_tab
                                                                          ();
   v_pol_dtls_rec        gin_load_policy_dtls_tbl
                                               := gin_load_policy_dtls_tbl
                                                                          ();
   v_new_ipu_code        NUMBER;
   v_pol_recd            web_pol_tab              := web_pol_tab ();
   v_poll_code           NUMBER;
   v_batch_no            NUMBER;
   v_user                VARCHAR2 (30)
            := pkg_global_vars.get_pvarchar2 ('PKG_GLOBAL_VARS.PVG_USERNAME');
   v_exceptions          VARCHAR2 (10);
   v_policy_no           VARCHAR2 (30);
   v_pol_wef             DATE;
   v_pol_wet             DATE;
   v_rn_id               NUMBER;
   v_batchno             NUMBER;
   v_trans_no            NUMBER;
   v_covt_type           VARCHAR2 (30);
   v_uw_yr               NUMBER;
   v_ipu_code            NUMBER;
   v_si                  NUMBER;
   v_tot_prem            NUMBER;
   v_pol_policy_status   VARCHAR2 (30);
   v_clnt_sht_desc       VARCHAR2 (30);
   v_pro_code            NUMBER;
   v_agn_status          VARCHAR2 (30);
   v_user1               VARCHAR2 (30);
   v_agn_sht_desc        VARCHAR2 (30);

   CURSOR taxes (vbatchno IN NUMBER)
   IS
      SELECT *
        FROM gin_policy_taxes
       WHERE ptx_pol_batch_no = vbatchno;

   v_tl                  NUMBER;
   v_phfund              NUMBER;
   v_scl_code            NUMBER;
   v_covt_code           NUMBER;
   v_pro_sht_desc        VARCHAR2 (10);
   v_risk_ri_rec         web_risk_tab             := web_risk_tab ();
   v_auth                VARCHAR2 (10);
   v_clnt_name           VARCHAR2 (300);

   -- v_clnt_sht_desc  VARCHAR2 (30);
   CURSOR drpol (v_policy_no IN VARCHAR2)
   IS
      SELECT pol_batch_no
        FROM gin_policies
       WHERE pol_policy_no = v_policy_no AND NVL (pol_authosrised, 'N') !=
                                                                          'A';

   CURSOR rsk
   IS
      SELECT *
        FROM gin_policy_electronic_data
       WHERE ped_code = v_ped_code;

   CURSOR risk (vbatch IN NUMBER)
   IS
      SELECT *
        FROM gin_insured_property_unds
       WHERE ipu_pol_batch_no = vbatch;
BEGIN
   /*This function create a policy  from client creation to authorization*/
   v_user1 := fms_gen_pkg.get_username ();

   IF policyagentcode != 0
   THEN
      BEGIN
         SELECT agn_status
           INTO v_agn_status
           FROM tqc_agencies
          WHERE agn_code = TRIM (policyagentcode);
      EXCEPTION
         WHEN OTHERS
         THEN
            raise_error ('Error determining the account status....');
      END;

functions.get_policy_facre:
FUNCTION  get_policy_facre(v_batch_no in number) return NUMBER 
    IS

    v_count NUMBER;
BEGIN
        BEGIN

              SELECT COUNT (1)
              INTO v_count
              FROM gin_policies
             WHERE pol_policy_type = 'F' AND pol_batch_no = v_batch_no;

            RETURN UPPER(v_count);
        EXCEPTION
                WHEN OTHERS THEN 
                    RAISE_APPLICATION_ERROR(-20001,'This policy is not a Facre business....');

        END;

functions.get_installment_period:
function get_installment_period(v_installment_def in VARCHAR2, v_no_of_endos in NUMBER,v_value_type in VARCHAR2 default 'C') return  NUMBER
   is
   v_installment_perct number;
   begin
   ---raise_error('v_installment_def='||v_installment_def||'v_no_of_endos='||v_no_of_endos||'v_installment_perct='||v_installment_perct);
      IF v_value_type='C' THEN
       select column_value into v_installment_perct   from(
                    select rownum count,x.* from table(convertstringtoarray(v_installment_def))  x)a 
                      where a.count=v_no_of_endos;   
      ELSIF v_value_type='E' THEN
      select  SUM(column_value)  INTO v_installment_perct  from table(convertstringtoarray(v_installment_def)) 
                      WHERE ROWNUM<=v_no_of_endos;
      END IF;

   return v_installment_perct;

   end;

